function encryptRSA(plaintext) {
  const {
    java
  } = this;
  let cipher = java.createAsymmetricCrypto("RSA/ECB/PKCS1Padding").setPublicKey(java.base64DecodeToByteArray(publicKey));
  let encrypted = cipher.encryptBase64(plaintext);
  return encrypted
}

/**
