//获取源变量
function getData() {
  const {
    source
  } = this;
  try {
    return JSON.parse(source.getVariable());
  } catch (err) {
    return {};
  }
}

//保存源变量 setData(data)
function setData(obj) {
  const {
    source
  } = this;
  if (typeof obj == "object") {
    source.setVariable(JSON.stringify(obj, null, 1));
  }
}

function URL(path, body, isLogin) {
  const {
    java,
    source
  } = this;
  let method = body ? 'POST' : 'GET';
  let data = this.getData();

  let timestamp = Date.now();
  let headers = {
    "ts": timestamp,
  }
  let headerKeys = ["x-version", "user-agent", "appid", "authentication"];
  if (isLogin) {
    config.oaid = body.symbol || data.oaid || java.randomUUID();
    if (body) body = this.xEncrypt(body);
  } else {
    headerKeys.push("x-token");
    config.oaid = data.oaid;
    config['x-token'] = data.token;
  }
  config.authentication = this.encrypt(java.base64Encode(`2.4.7.9-${timestamp}-Android-${config.appVersion}-${config.oaid}`));
  for (let hkey of headerKeys) {
    config[hkey] && (headers[hkey] = config[hkey]);
  }

  let option = {
    "method": method,
    "headers": headers
  }
  if (body) {
    option.headers['Content-Type'] = 'application/json; charset=utf-8';
    option.body = typeof body == 'object' ? JSON.stringify(body) : body;
  }
  let url = `${/^http/.test(path) ? '' : config.BASEURL}${path},${JSON.stringify(option)}`;
  // java.log(url);
  return url;
}

