/**
 * 登录
 */
function login(type) {
  try {
    let loginInfo = source.getLoginInfoMap() || result || {};
    if (type === undefined) {
      return false
    }
    /*
    if (getData().token) {
      java.toast("已登录 请勿重复登录");
      return false
    }
    */
    java.toast("登录中...");
    let loginName = String(loginInfo["账号"] || "");
    let password = String(loginInfo["密码"] || "");

    let is_RBQ_login = loginName == "" || password == "";
    if (is_RBQ_login) {
      loginName = RBQ_account.loginName;
      password = RBQ_account.password;
    }

    let isPhone = /^1\d{10}/.test(loginName);
    let body = {};
    body[isPhone ? "phone" : "email"] = loginName;
    body = Object.assign(body, {
      "password": password,
      "enum": isPhone ? 0 : 1,
      "symbol": java.randomUUID()
    });
    let url = URL(`/app/users/login`, body, true);
    let r1 = String(java.ajax(url)).split('.');
    r1 = this.decrypt(r1[1], r1[0]);
    r1 = JSON.parse(r1);
    // java.log("[loginName]:"+loginName)
    // java.log("[login]:" + JSON.stringify(r1));
    if (r1.code == 20000) {
      java.toast(loginName + '登录成功!');
      let data = getData();
      data.loginName = loginName;
      data.oaid = r1.data.id;
      data.token = r1.data.token;
      let Payload = JSON.parse(java.base64Decode(data.token.split(".")[1]));
      data.timestamp = Payload.exp * 1000;
      data.time = java.timeFormat(data.timestamp);
      setData(data);
      if (!is_RBQ_login) {
        source.putLoginInfo(JSON.stringify(loginInfo));
      }
    } else {
      java.toast(r1.message);
    }
  } catch (e) {
    java.log(e)
  }
}

/**
* 退出登录
*/
function logout() {
  let loginInfo = result;
  let data = getData();
  data.loginName = "";
  data.token = "";
  data.oaid = "";
  data.time = "";
  data.timestamp = "";
  setData(data);

  java.toast("退出登录成功");
}

function saveKey() {
  let data = getData();
  data.key = String(result["搜索关键词"]) || "";
  setData(data);
  java.toast("保存关键词成功,在列表页下拉刷新")
}

